import {
  connectMongoDbAsyncLog,
  saveToCollectionAsync,
  collection,
  deleteCollectionAsyncLog,
  closeConnection
} from 'mongo-quick';
// ────────────────────────────────────────────────────────
(async function () {
  const itemCollection2020 = collection("itemCollection2020", { itemModel: String, itemAuthor: String, })
  
  console.log(await connectMongoDbAsyncLog()) // Tip: This automatically picks the database uri of MONGO_DB_URI from .env file.
  
  await saveToCollectionAsync(itemCollection2020, { itemModel: 'Some title...', itemAuthor: "Some author name..." })
  
  console.log(await deleteCollectionAsyncLog(itemCollection2020))
  
  closeConnection()
})();