import mongoose from 'mongoose'
import { connectMongoDb } from "../mongoUtilities.js";
import { userCollection } from "../collections.js";
// ────────────────────────────────────────────────────────
(async function () {
  console.log(await connectMongoDb())
  const result = await userCollection.deleteMany({})
  console.log('SUCCESS: Collection Deleted!')
  console.log('INFO: Deletion Details:', result)
  mongoose.connection.close()
  console.log('--------------');
  console.log('INFO: Connection closed :) ')
  console.log('--------------');
})();
